import React, { useEffect } from "react";
import logo from "./logo.svg";
import "./App.css";
import "@glorious/demo/dist/gdemo.min.css";

import CodeBox from "react-code-box/dist/index";
import Prism from "prismjs";
const highlight = require("cli-highlight").highlight;

function App() {
  let code = `
  class TodoList extends Component {
      state = { todos: [], text: '' };
      updateText = e => {
          this.setState({ text: e.target.value });
      };
      addItem = () => {
          let { todos, text } = this.state;
          todos = todos.concat({ text });
          this.setState({ todos, text: '' });
      };
      render({ }, { todos, text }) {
          return (
              <div>
                  <form onSubmit={this.addItem} action="javascript:">
                      <input value={text} onInput={this.updateText} />
                      <input type="submit" value="Add" />
                  </form>
                  <ul>
                      { todos.map( todo => (
                          <li>{todo.text}</li>
                      )) }
                  </ul>
              </div>
          );
      }
  }

  render(<TodoList />);
  `;

  useEffect(()=>{
    //@ts-ignorev

    document.querySelector(".language-js")!.innerHTML = Prism.highlight(
      code,
      Prism.languages.jsx,
    );
  })
  // Returns a highlighted HTML string

  return (
    <pre><code className="language-js"></code></pre>
  )
  // return (
  //   <div className="App">
  //     das
  //     <CodeBox title="hahha" variant="dark" code={code} />
  //   </div>
  // );
}

export default App;
